const initialValue = [];
export default (posts = initialValue, action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return [...posts, action.payload];
    case "DELETE":
      return posts.filter(post => post.id !== action.payload);
    default:
      return posts;
  }
};
